def writeEvery400():
    input_file = "cities_list.txt"
    output_base = "output_file"
    batch_size = 400

    # initialize variables
    batch_num = 1
    line_num = 0
    output_file = f"{output_base}_{batch_num}.txt"

    # open input and output files
    with open(input_file, 'r') as f_in, open(output_file, 'w') as f_out:
        for line in f_in:
            # write line to output file
            f_out.write(line)
            line_num += 1

            # if we've reached the batch size, close current output file and start a new one
            if line_num % batch_size == 0:
                f_out.close()
                batch_num += 1
                output_file = f"{output_base}_{batch_num}.txt"
                f_out = open(output_file, 'w')

