def writeLocationToCsv():
    not_found = []

    # create a CSV file to write the results to
    with open('city_locations.csv', mode='a', newline='') as file:
        writer = csv.writer(file)
        writer.writerow(['CityName', 'Latitude', 'Longitude'])

        # loop through the cities and get their locations
        for city in cities:
            geolocator = Nominatim(user_agent="my-app", timeout=10)
            location = geolocator.geocode(city)

            # write the city and location to the CSV file
            if location is not None:
                latitude = location.latitude
                longitude = location.longitude
                writer.writerow([city, latitude, longitude])
            else:
                not_found.append(city)
                # writer.writerow([city, 'Not found', 'Not found'])

    print("Not found: \n")
    for n in not_found:
        print(n)

